import React, { Component } from "react";
import http from "./httpService";
class Prod2 extends Component {
  state = {
    product: { productName: "", category: "",description:"" },
    edit:false,
    errors:{},
  };
  async componentDidMount(){
    this.fetchData();
  }
  async componentDidUpdate(prevProps,prevState){
   if(prevProps !== this.props) this.fetchData();
  }
  async fetchData(){
    const {id} = this.props.match.params;
    if(id){
      let response = await http.get(`/products/${id}`);
      let {data} = response;
      this.setState({product : data, edit:true});
    }
    else{
     let  product = {  productName: "", category: "",description:"" };
     this.setState ({ product : product, edit:false});
    }
  }


 handleChange = (e) => {
    const { currentTarget: input } = e;
    let s1 = { ...this.state };
    s1.product[input.name] = input.value;
    this.handleValidate(e);
    this.setState(s1);
  };
 async postData(url, obj) {
    let response = await http.post(url, obj);
    let {data} = response;
    console.log(data);
    this.props.history.push("/products/View");
  }

  async putData(url, obj) {
    let response = await http.put(url, obj);
    let {data} = response;
    console.log(data);
    this.props.history.push("/products/View");
  }

  handleSubmit = (e) =>{
    e.preventDefault();
    let errors = this.validateAll();
    if(this.isValid(errors)){
        let {product,edit} = this.state;
        edit ? this.putData(`/products/${product.productId}`,product) : this.postData("/products", product);
    }
    else{
        let s1={...this.state};
        s1.errors = errors ;
         this.setState(s1);
    }
 };

 handleValidate=(e) =>{
  let {currentTarget: input} = e;
  let s1 = {...this.state};
  switch(input.name) {
    case "productName" : s1.errors.productName = this.validateName(input.value); break;
    case "category" : s1.errors.category = this.validateCat(input.value); break;
    case "description" : s1.errors.description = this.validateDesc(input.value); break;
    default: break;
  }
  this.setState(s1);
 };

 isValid=(errors)=>{
  let keys = Object.keys(errors);
  let count = keys.reduce((acc,curr)=> errors[curr] ? acc+1 : acc ,0);
  return count===0;
 };
isFormValid=() =>{
let errors = this.validateAll();
return this.isValid(errors);
};

 validateAll = () =>{
 let { productName,category,description} = this.state.product;
 let errors = {};
 errors.productName = this.validateName(productName);
 errors.category = this.validateCat(category);
 errors.description = this.validateDesc(description);
 return errors;
 };
 validateName = (productName) => !productName ? "Name must be entered" : "";
 validateCat = (category) => !category ? "Category must be entered" : "";
 validateDesc = (description) => !description ? "description must be entered" : "";


 render() {
    let { productName,category,description } = this.state.product;
    let {errors,edit} = this.state;
    return (
      <div className="container">
        {edit ?
        <div className="form-group mt-2">
        <label>Name</label>
        <input
          type="text"
          className="form-control"
          id="productName"
          name="productName"
          readOnly
          placeholder="Enter Product Name"
          value={productName}
          onBlur={this.handleValidate}
          onChange={this.handleChange}
       />
      </div>
        : <div className="form-group mt-2">
          <label>Name</label>
          <input
            type="text"
            className="form-control"
            id="productName"
            name="productName"
            placeholder="Enter Product Name"
            value={productName}
            onBlur={this.handleValidate}
            onChange={this.handleChange}
         />
        {errors.productName ? <span className="text-danger">{errors.productName}</span> : ""}
        </div>}
        <br/>
        <div className="form-group">
          <label>Category</label>
          <input
            type="text"
            className="form-control"
            id="category"
            name="category"
            placeholder="Enter category"
            value={category}
            onBlur={this.handleValidate}
            onChange={this.handleChange}
         />
        {errors.category ? <span className="text-danger">{errors.category}</span> : ""}
        </div>
        <br/>
        <div className="form-group">
          <label>Description</label>
          <input
            type="text"
            className="form-control"
            id="description"
            name="description"
            placeholder="Enter description"
            value={description}
            onBlur={this.handleValidate}
            onChange={this.handleChange}
         />
        {errors.description ? <span className="text-danger">{errors.description}</span> : ""}
        </div>
        <br/>

        <button className="btn btn-primary mt-2" disabled={!this.isFormValid()} onClick={this.handleSubmit}>
          Submit
        </button>
      </div>
    );
  }
}
export default Prod2;
