import React, { Component } from "react";
import http from "./httpService";
class Prod1 extends Component {
state = {
    products : [],
   };
  async fetchData() {
    let response = await http.get(`/products`);
    let { data } = response;
    console.log(data);
    this.setState({ products: data });
  }
  componentDidMount() {
    this.fetchData();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps !== this.props) this.fetchData();
  }

  
 handleEdit(id){
    this.props.history.push(`/products/${id}/edit`);
 }
 
 handlePurchase(id){
    this.props.history.push(`/purchases/products/${id}`);
 }
 handleTotalPurchase(id){
    this.props.history.push(`/totalPurchase/product/${id}`);
 }

  render() {
    const { products} = this.state;
    return (
      <div className="container">
        
        <div className="row bg-info text-center">
         <div className="col-1 border">ProductId</div>
          <div className="col-2 border">Name</div>
          <div className="col-2 border">Category</div>
          <div className="col-2 border">Description</div>
          <div className="col-1 border"></div>
          <div className="col-2 border"></div>
          <div className="col-2 border"></div>
        </div>
        {products.map((s) => (
          <div className="row text-center" key={s.productId}>
            <div className="col-1 border">{s.productId}</div>
           <div className="col-2 border">{s.productName}</div>
           <div className="col-2 border">{s.category}</div>
           <div className="col-2 border">{s.description}</div>
           <div className="col-1 border"><button className="btn btn-info btn-sm" onClick={()=>this.handleEdit(s.productId)}>Edit</button></div>
           <div className="col-2 border"><button className="btn btn-warning btn-sm" onClick={()=>this.handlePurchase(s.productId)}>Purchases</button></div>
           <div className="col-2 border"><button className="btn btn-danger btn-sm" onClick={()=>this.handleTotalPurchase(s.productId)}>Total Purchases</button></div>
          </div>
        ))}
        </div>
    );
  }
}
export default Prod1;
