import React, { Component } from "react";
import http from "./httpService";
class Shop2 extends Component {
  state = {
    shop: { name: "", rent: "" },
    edit:false,
    errors:{},
  };
  async componentDidMount(){
    this.fetchData();
  }
  async componentDidUpdate(prevProps,prevState){
   if(prevProps !== this.props) this.fetchData();
  }
  async fetchData(){
    const {id} = this.props.match.params;
    if(id){
      let response = await http.get(`/shops/${id}`);
      let {data} = response;
      this.setState({shop : data, edit:true});
    }
    else{
     let  shop = { name: "", rent: "" };
     this.setState ({ shop : shop, edit:false});
    }
  }


 handleChange = (e) => {
    const { currentTarget: input } = e;
    let s1 = { ...this.state };
    s1.shop[input.name] = input.value;
    this.handleValidate(e);
    this.setState(s1);
  };
 async postData(url, obj) {
    let response = await http.post(url, obj);
    let {data} = response;
    console.log(data);
    this.props.history.push("/shops/View");
  }

  async putData(url, obj) {
    let response = await http.put(url, obj);
    let {data} = response;
    console.log(data);
    this.props.history.push("/shops/View");
  }

  handleSubmit = (e) =>{
    e.preventDefault();
    let errors = this.validateAll();
    if(this.isValid(errors)){
        let {shop,edit} = this.state;
        edit ? this.putData(`/shops/${shop.shopId}`,shop) : this.postData("/shops", shop);
    }
    else{
        let s1={...this.state};
        s1.errors = errors ;
         this.setState(s1);
    }
 };

 handleValidate=(e) =>{
  let {currentTarget: input} = e;
  let s1 = {...this.state};
  switch(input.name) {
    case "name" : s1.errors.name = this.validateName(input.value); break;
    case "rent" : s1.errors.rent = this.validateRent(input.value); break;
    default: break;
  }
  this.setState(s1);
 };

 isValid=(errors)=>{
  let keys = Object.keys(errors);
  let count = keys.reduce((acc,curr)=> errors[curr] ? acc+1 : acc ,0);
  return count===0;
 };
isFormValid=() =>{
let errors = this.validateAll();
return this.isValid(errors);
};

 validateAll = () =>{
 let { name,rent} = this.state.shop;
 let errors = {};
 errors.name = this.validateName(name);
 errors.rent = this.validateRent(rent);
 return errors;
 };
 validateName = (name) => !name ? "Name must be entered" : "";
 validateRent = (rent) => !rent ? "Rent must be entered" : "";
 render() {
    let { name,rent } = this.state.shop;
    let {errors} = this.state;
    return (
      <div className="container">
         <div className="form-group mt-2">
          <label>Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            placeholder="Enter Shop Name"
            value={name}
            onBlur={this.handleValidate}
            onChange={this.handleChange}
         />
        {errors.name ? <span className="text-danger">{errors.name}</span> : ""}
        </div>
        <br/>
        <div className="form-group">
          <label>Rent</label>
          <input
            type="text"
            className="form-control"
            id="rent"
            name="rent"
            placeholder="Enter Rent"
            value={rent}
            onBlur={this.handleValidate}
            onChange={this.handleChange}
         />
        {errors.rent ? <span className="text-danger">{errors.rent}</span> : ""}
        </div>
        <br/>
          
        <button className="btn btn-primary mt-2" disabled={!this.isFormValid()} onClick={this.handleSubmit}>
          Submit
        </button>
      </div>
    );
  }
}
export default Shop2;
