import React, { Component } from "react";
import http from "./httpService";
class NewEmployee extends Component {
  state = {
    car: { id:"",price: "", kms: "", year: "", model: "", color:"" },
    ColorOpt : ["White","Steel Grey","Metallic Blue","Black","Red","Silver Grey"],
    ModelOpt :["Swift Dzire VXi","Etios SMi","City AXi","Swift DXi"],
    edit:false,
  };
  async componentDidMount(){
    this.fetchData();
  }
  async componentDidUpdate(prevProps,prevState){
   if(prevProps !== this.props) this.fetchData();
  }
  async fetchData(){
    const {id} = this.props.match.params;
    console.log(id);
    if(id){
      let response = await http.get(`/cars/${id}`);
      let {data} = response;
      this.setState({car : data, edit:true});
    }
    else{
     let  car = { id:"",price: "", kms: "", year: "", model: "", color:"" };
     this.setState ({ car:car, edit:false});
    }
  }
  handleChange = (e) => {
    const { currentTarget: input } = e;
    let s1 = { ...this.state };
    s1.car[input.name] = input.value;
    this.setState(s1);
  };

  async postData(url, obj) {
    let response = await http.post(url, obj);
    let {data} = response;
    console.log(data);
    this.props.history.push("/cars");
  }
  async putData(url, obj) {
    let response = await http.put(url, obj);
    let {data} = response;
    console.log(data);
    this.props.history.push("/cars");
  }

  handleSubmit = (e) => {
    e.preventDefault();
    let {car,edit} = this.state;
    edit
     ? this.putData(`/cars/${car.id}`,car) 
     : this.postData("/cars", car);
  };

  render() {
    let { id,price, kms, year, model, color } = this.state.car;
    const { ColorOpt,ModelOpt,edit} = this.state;
    return (
      <div className="container">
        {edit ?
        <div className="form-group mt-2">
        <label>Car Id</label>
        <input
          type="text"
          className="form-control"
          id="id"
          name="id"
          disabled
          placeholder="Enter Id"
          value={id}
          onChange={this.handleChange}
        />
      </div> :
        <div className="form-group mt-2">
          <label>Car Id</label>
          <input
            type="text"
            className="form-control"
            id="id"
            name="id"
            placeholder="Enter Id"
            value={id}
            onChange={this.handleChange}
          />
        </div>}
        <br/>
        <div className="form-group">
        <label>Price</label>
        <input
          type="text"
          className="form-control"
          id="price"
          name="price"
          placeholder="Enter Price"
          value={price}
          onChange={this.handleChange}
        />
      </div><br/>
      <div className="form-group">
        <label>Mileage in kms</label>
        <input
          type="text"
          className="form-control"
          id="kms"
          name="kms"
          placeholder="Enter Mileage"
          value={kms}
          onChange={this.handleChange}
        />
      </div><br/>
      <div className="form-group">
        <label>Year of manufacture</label>
        <input
          type="text"
          className="form-control"
          id="year"
          name="year"
          placeholder="Enter Year"
          value={year}
          onChange={this.handleChange}
        />
      </div><br/>
        <div className="form-group">
        <label>Model</label>
          <select
            className="form-control"
            id="model"
            name="model"
            value={model}
            onChange={this.handleChange}
          >
            <option disabled value="">Select the Model</option>
            {ModelOpt.map((opt) => (
              <option>{opt}</option>
            ))}
          </select>
          <br />
        </div>
        <div className="form-group">
        <label>Color</label>
          <select
            className="form-control"
            id="color"
            name="color"
            value={color}
            onChange={this.handleChange}
          >
            <option disabled value="">Select the Color</option>
            {ColorOpt.map((opt) => (
              <option>{opt}</option>
            ))}
          </select>
          <br />
        </div>
          
        <button className="btn btn-primary mt-2" onClick={this.handleSubmit}>
          Submit
        </button>
      </div>
    );
  }
}
export default NewEmployee;
