import React, { Component } from "react";
class OptionsProd extends Component {
  handleChange = (e) => {
    let { currentTarget: input } = e;
    let options = { ...this.props.options };
    input.type==="checkbox" ?
    options[input.name] = this.updateCBs(
      options[input.name],
      input.checked,
      input.value
    )
     : options[input.name] = input.value;
    this.props.onOptionChange(options);
};
updateCBs = (inputValue, checked, value) => {
   let inpArr = inputValue ? inputValue.split(",") : [];
    if (checked) inpArr.push(value); 
    else {
      let index = inpArr.findIndex((ele) => ele === value);
      if (index >= 0) inpArr.splice(index, 1);
    }
    return inpArr.join(",");
};

makeCheckboxes = (arr, values, name, label) => (
   <React.Fragment>
      <label className="form-check-label fw-bold">{label}</label>
      {arr.map((opt, index) => (
        <div className="form-check" key={index}>
          <input
            className="form-check-input"
            value={opt}
            type="checkbox"
            name={name}
            checked={values.findIndex((val) => val === opt) >= 0}
            onChange={this.handleChange}
          />
          <label className="form-check-label">{opt}</label>
        </div>
      ))}
    </React.Fragment>
  );
  makeDropdown = (arr, value, name, label) => (
    <div className="form-group">
      <select
        className="form-control"
        name={name}
        value={value}
        onChange={this.handleChange}
      >
        <option className="fw-bold" value="">{label}</option>
        {arr.map((opt) => (
          <option>{opt}</option>
        ))}
      </select>
      <br />
    </div>
  );
    
  render() {
    let {products="",store="",sort=""} = this.props.options;
    let { prod, storeArr,sortArr} = this.props;
    return (
      <div className="container border bg-light">
      <div className="row">
      <div className="col-12">
      {this.makeCheckboxes(
            prod,
            products.split(","),
            "products",
            "Products"
          )}
      </div> 
      </div> <br/>
      <div className="col-12">
          {this.makeDropdown(
            storeArr,
            store,
            "store",
            "Select Store"
          )}
        </div>
        <div className="col-12">
          {this.makeDropdown(
            sortArr,
            sort,
            "sort",
            "Sort By"
          )}
        </div>

        </div> 
        
    );
  }
}
export default OptionsProd;
