import React, { Component } from "react";
import http from "./httpService";
class Shops1 extends Component {
state = {
    shops : [],
   };
  async fetchData() {
    let response = await http.get(`/shops`);
    let { data } = response;
    console.log(data);
    this.setState({ shops: data });
  }
  componentDidMount() {
    this.fetchData();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps !== this.props) this.fetchData();
  }

  
 handlePurchase(id){
    this.props.history.push(`/purchases/shops/${id}`);
 }
 handleTotalPurchase(id){
    this.props.history.push(`/totalPurchase/shop/${id}`);
 }

  render() {
    const { shops} = this.state;
    return (
      <div className="container">
        
        <div className="row bg-info text-center">
         <div className="col-2 border">ShopId</div>
          <div className="col-3 border">Name</div>
          <div className="col-3 border">Rent</div>
          <div className="col-2 border"></div>
          <div className="col-2 border"></div>
        </div>
        {shops.map((s) => (
          <div className="row text-center" key={s.shopId}>
            <div className="col-2 border">{s.shopId}</div>
           <div className="col-3 border">{s.name}</div>
           <div className="col-3 border">{s.rent}</div>
           <div className="col-2 border"><button className="btn btn-warning btn-sm" onClick={()=>this.handlePurchase(s.shopId)}>Purchases</button></div>
           <div className="col-2 border"><button className="btn btn-danger btn-sm"  onClick={()=>this.handleTotalPurchase(s.shopId)}>Total Purchases</button></div>
          </div>
        ))}
        </div>
    );
  }
}
export default Shops1;
