import React, { Component } from "react";
import http from "./httpService";
import { Link } from "react-router-dom";
class Emps extends Component {
state = {
  data: {},
};
async fetchData() {
    let queryParams = queryString.parse(this.props.location.search);
    let searchStr = this.makeSearchString(queryParams);
    console.log(searchStr);
    let { page = "1" } = queryParams;
    let response = await http.get(`/sporticons/stars?${searchStr}`);
    let { data } = response;
    console.log(response);
    this.setState({ data: data });
  }
componentDidMount() {
  this.fetchData();
};
componentDidUpdate(prevProps, prevState) {
  if (prevProps !== this.props) this.fetchData();
};

  handlePage = (incr) => {
    let queryParams = queryString.parse(this.props.location.search);
    let { page = "1" } = queryParams;
    let newPage = +page + incr;
    queryParams.page = newPage;
    this.callURL(`/sporticons/stars`, queryParams);
  };

  handleOptionChange = (options) => {
    options.page="1";
    this.callURL("/sporticons/stars", options);
  };
  
    callURL = (url, options) => {
      let searchString = this.makeSearchString(options);
      this.props.history.push({
        pathname: url,
        search: searchString,
      });
    };
  
    makeSearchString = (options) => {
      let { page ,countries} = options;
      let searchStr = "";
      searchStr = this.addToQueryString(searchStr, "page", page);
      searchStr = this.addToQueryString(searchStr, "countries", countries);
      return searchStr;
    };
    addToQueryString = (str, paramName, paramValue) =>
      paramValue
        ? str
          ? `${str}&${paramName}=${paramValue}`
          : `${paramName}=${paramValue}`
        : str;
  
  render() {
    const { stars=[]} = this.state.data;
    return (
      <div className="container">
          <div className="col-9">
        <div className="row bg-info">
          <div className="col-4">Name</div>
          <div className="col-4">Country</div>
          <div className="col-4">Sport</div>
        </div>
        {stars.map((s) => (
          <div className="row" key={s.id}>
            <div className="col-4 border"><Link to={`/sporticons/details/${s.id}`}>{s.name}</Link></div>
            <div className="col-4 border">{s.country}</div>
            <div className="col-4 border">{s.sport}</div>
          </div>
        ))}
        </div>
      </div>
    );
  }
}
export default Emps;
