import React, { Component } from "react";
import http from "./httpService";
import queryString from "query-string";
import OptionsProd from "./optionsProd";
class Purchase extends Component {
  state = {
    purchases: [],
    prod:['1','2','3','4','5','6','7','8','9'],
    storeArr : ['1','2','3','4','5'],
  };
  async fetchData() {
    let queryParams = queryString.parse(this.props.location.search);
    let searchStr = this.makeSearchString(queryParams);
    let response = await http.get(`/purchases?${searchStr}`);
    let { data } = response;
    console.log(data);
    this.setState({ purchases: data});
  }
  componentDidMount() {
    this.fetchData();
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevProps !== this.props) this.fetchData();
  }

  handleOptionChange = (options) => {
    this.callURL("/purchases", options);
  };
  
    callURL = (url, options) => {
      let searchString = this.makeSearchString(options);
      console.log(searchString);
      this.props.history.push({
        pathname: url,
        search: searchString,
      });
    };
  
    makeSearchString = (options) => {
      let { products,store } = options;
      let searchStr = "";
      searchStr = this.addToQueryString(searchStr, "products",products);
      searchStr = this.addToQueryString(searchStr, "store",store);
      return searchStr;
    };
    addToQueryString = (str, paramName, paramValue) =>
      paramValue
        ? str
          ? `${str}&${paramName}=${paramValue}`
          : `${paramName}=${paramValue}`
        : str;
  
  
  render() {
    const { purchases ,prod ,storeArr} = this.state;
    let queryParams = queryString.parse(this.props.location.search);
    
    return (
      <div className="container">
        <div className="row">
          <div className="col-2">
            <OptionsProd
              options={queryParams}
              prod={prod}
              storeArr={storeArr}
              onOptionChange={this.handleOptionChange}
            />
          </div>
          <div className="col-10">
            <div className="row bg-info text-center">
              <div className="col-3 border">PurchaseId</div>
              <div className="col-3 border">ShopId</div>
              <div className="col-2 border">ProductId</div>
              <div className="col-2 border">Quantity</div>
              <div className="col-2 border">Price</div>
            </div>
            {purchases.map((s) => (
              <div className="row text-center" key={s.purchaseId}>
                <div className="col-3 border">{s.purchaseId}</div>
                <div className="col-3 border">{s.shopId}</div>
                <div className="col-2 border">{s.productid}</div>
                <div className="col-2 border">{s.quantity}</div>
                <div className="col-2 border">{s.price}</div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}
export default Purchase;
